import React, {useState} from 'react';
import api from '../api';

export default function AuthForm(){
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({name:'', email:'', password:'', role:'TENANT'});

  function change(e){ setForm({...form, [e.target.name]: e.target.value}); }

  async function submit(e){
    e.preventDefault();
    try {
      if(isLogin){
        const res = await api.post('/auth/login', { email: form.email, password: form.password });
        const { token, user } = res.data;
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        alert('Login successful');
      } else {
        const res = await api.post('/auth/register', form);
        const { token, user } = res.data;
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        alert('Registered and logged in');
      }
    } catch(err){
      console.error(err);
      alert(err?.response?.data?.error || 'Auth error');
    }
  }

  return (
    <div>
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <form onSubmit={submit}>
        {!isLogin && <input name="name" placeholder="Name" value={form.name} onChange={change} required />}
        <input name="email" placeholder="Email" value={form.email} onChange={change} required />
        <input name="password" placeholder="Password" value={form.password} onChange={change} type="password" required />
        {!isLogin && (
          <select name="role" value={form.role} onChange={change}>
            <option value="TENANT">Tenant</option>
            <option value="OWNER">Owner</option>
            <option value="AGENT">Agent</option>
          </select>
        )}
        <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
      </form>
      <button onClick={()=>setIsLogin(!isLogin)}>{isLogin ? 'Need an account? Register' : 'Have account? Login'}</button>
    </div>
  );
}
