import React, { useEffect, useState } from "react";
import { getAdminProperties, toggleProperty } from "../api";

export default function AdminPanel() {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProperties();
  }, []);

  async function fetchProperties() {
    try {
      setLoading(true);
      const data = await getAdminProperties();
      setProperties(data);
    } catch (err) {
      console.error("Error fetching admin properties:", err);
      alert("Failed to load admin properties: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleToggle(id) {
    try {
      await toggleProperty(id);
      alert("Property availability updated!");
      fetchProperties(); // refresh list
    } catch (err) {
      console.error("Error toggling property:", err);
      alert("Failed to update property: " + err.message);
    }
  }

  if (loading) return <div>Loading admin properties...</div>;

  return (
    <div>
      <h2>Admin Panel - Manage Properties</h2>
      {properties.length === 0 ? (
        <p>No properties found.</p>
      ) : (
        <table border="1" cellPadding="6" style={{ marginTop: 10 }}>
          <thead>
            <tr>
              <th>Title</th>
              <th>City</th>
              <th>Type</th>
              <th>Price</th>
              <th>Available</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {properties.map((p) => (
              <tr key={p.id}>
                <td>{p.title}</td>
                <td>{p.city}</td>
                <td>{p.type}</td>
                <td>{p.price}</td>
                <td>{p.available ? "Yes" : "No"}</td>
                <td>
                  <button onClick={() => handleToggle(p.id)}>
                    {p.available ? "Mark Unavailable" : "Mark Available"}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
