import React, { useState } from "react";
import { Link } from "react-router-dom";

// Make sure your base URL and port are correct
const API_BASE_URL = "http://localhost:8080/api/properties";

export default function PropertyList() {
  // --- STATE MANAGEMENT ---
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(false);

  // New state for all filters
  const [filters, setFilters] = useState({
    city: "",
    type: "",
    minPrice: "",
    maxPrice: "",
  });

  const token = localStorage.getItem("token");

  // --- API CALL ---
  async function fetchProperties() {
    setLoading(true);

    // Use URLSearchParams to build the query string dynamically
    const params = new URLSearchParams();
    if (filters.city) params.append("city", filters.city);
    if (filters.type) params.append("type", filters.type);
    if (filters.minPrice) params.append("min", filters.minPrice);
    if (filters.maxPrice) params.append("max", filters.maxPrice);

    // Construct the final URL for the /search endpoint
    const url = `${API_BASE_URL}/search?${params.toString()}`;

    try {
      const res = await fetch(url, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        }
      });

      if (!res.ok) {
        throw new Error(`Failed to fetch: ${res.status} ${res.statusText}`);
      }

      const data = await res.json();
      setProperties(data);
    } catch (err) {
      console.error(err);
      alert("Failed to load properties: " + err.message);
      setProperties([]); // Clear properties on error
    } finally {
      setLoading(false);
    }
  }

  // --- HANDLER FUNCTIONS ---
  function handleInputChange(e) {
    const { name, value } = e.target;
    setFilters(prevFilters => ({
      ...prevFilters,
      [name]: value,
    }));
  }

  function handleSearch(e) {
    e.preventDefault(); // Prevent form submission if it's in a <form> tag
    fetchProperties();
  }

  function handleReset() {
    setFilters({
      city: "",
      type: "",
      minPrice: "",
      maxPrice: "",
    });
    setProperties([]); // Also clear the current results
  }

  // --- JSX / RENDER ---
  return (
    <div className="container mx-auto p-4 max-w-6xl">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">Find Your Next Property</h2>

      {/* Search and Filter Form */}
      <div className="bg-white p-6 rounded-xl shadow-md mb-8">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 items-end">
          {/* City Input */}
          <div>
            <label htmlFor="city" className="block text-sm font-medium text-gray-700 mb-1">City</label>
            <input
              id="city"
              name="city"
              className="border-2 border-gray-300 rounded-lg px-4 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
              type="text"
              placeholder="e.g., Miami"
              value={filters.city}
              onChange={handleInputChange}
            />
          </div>

          {/* Property Type Select */}
          <div>
            <label htmlFor="type" className="block text-sm font-medium text-gray-700 mb-1">Type</label>
            <select
              id="type"
              name="type"
              className="border-2 border-gray-300 rounded-lg px-4 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={filters.type}
              onChange={handleInputChange}
            >
              <option value="">All Types</option>
              {/* IMPORTANT: These values must match your backend Enum (PropertyType) */}
              <option value="APARTMENT">Apartment</option>
              <option value="HOUSE">House</option>
              <option value="CONDO">Condo</option>
              <option value="VILLA">Villa</option>
            </select>
          </div>

          {/* Min & Max Price Inputs */}
          <div className="lg:col-span-2 grid grid-cols-2 gap-4">
            <div>
                <label htmlFor="minPrice" className="block text-sm font-medium text-gray-700 mb-1">Min Price</label>
                <input
                    id="minPrice"
                    name="minPrice"
                    className="border-2 border-gray-300 rounded-lg px-4 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
                    type="number"
                    placeholder="No Min"
                    value={filters.minPrice}
                    onChange={handleInputChange}
                />
            </div>
            <div>
                <label htmlFor="maxPrice" className="block text-sm font-medium text-gray-700 mb-1">Max Price</label>
                <input
                    id="maxPrice"
                    name="maxPrice"
                    className="border-2 border-gray-300 rounded-lg px-4 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
                    type="number"
                    placeholder="No Max"
                    value={filters.maxPrice}
                    onChange={handleInputChange}
                />
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end items-center mt-6 space-x-3">
            <button
            onClick={handleReset}
            className="bg-gray-400 text-white rounded-lg px-6 py-2 font-semibold hover:bg-gray-500 transition-colors"
            >
            Reset
            </button>
            <button
            onClick={handleSearch}
            className="bg-blue-600 text-white rounded-lg px-8 py-2 font-semibold hover:bg-blue-700 transition-colors"
            >
            Search
            </button>
        </div>
      </div>

      {/* Property Listing */}
      {loading ? (
        <div className="text-center p-4">Loading properties... ‚è≥</div>
      ) : properties.length === 0 ? (
        <p className="text-center text-gray-500 bg-gray-50 p-8 rounded-lg">No properties found. Try adjusting your filters.</p>
      ) : (
        <ul className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {properties.map((p) => (
            <li key={p.id} className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 overflow-hidden transform hover:-translate-y-1">
              <Link to={`/property/${p.id}`} className="block">
                <div className="p-5">
                  <h3 className="text-xl font-semibold text-gray-900 mb-1 truncate">{p.title}</h3>
                  <p className="text-gray-600 font-medium mb-3 truncate">{p.address}</p>
                  <p className="text-sm text-gray-500 mb-4">
                    <span className="font-semibold">{p.city}</span> | <span className="uppercase font-mono text-xs">{p.type}</span>
                  </p>
                  <div className="flex justify-between items-center">
                    <span className="text-2xl font-bold text-blue-600">${p.price.toLocaleString()}</span>
                    <span className={`px-3 py-1 text-xs font-bold rounded-full ${p.available ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                      {p.available ? 'Available' : 'Occupied'}
                    </span>
                  </div>
                </div>
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
