// src/components/PropertyForm.js
import React, { useState } from "react";
import { createProperty } from "../api";

export default function PropertyForm() {
  // Get logged-in user from localStorage
  const storedUser = localStorage.getItem("user");
  const user = storedUser ? JSON.parse(storedUser) : null;

  const [form, setForm] = useState({
    title: "",
    description: "",
    address: "",
    city: "",
    type: "HOUSE",
    price: "",
    available: true,
  });

  const [loading, setLoading] = useState(false);

  function change(e) {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value,
    });
  }

  async function submit(e) {
    e.preventDefault();

    if (!user) {
      return alert("You must be logged in as OWNER or ADMIN to post a property.");
    }

    setLoading(true);

    try {
      // Parse price to number
      const payload = { ...form, price: Number(form.price) };

      const res = await createProperty(payload, user.id);
      alert(`Property "${res.title}" posted successfully!`);
      // Clear form
      setForm({
        title: "",
        description: "",
        address: "",
        city: "",
        type: "HOUSE",
        price: "",
        available: true,
      });
    } catch (err) {
      console.error(err);
      alert("Failed to post property: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div>
      <h2>Post a Property</h2>
      <form onSubmit={submit}>
        <input
          name="title"
          placeholder="Title"
          value={form.title}
          onChange={change}
          required
        />
        <input
          name="description"
          placeholder="Description"
          value={form.description}
          onChange={change}
          required
        />
        <input
          name="address"
          placeholder="Address"
          value={form.address}
          onChange={change}
          required
        />
        <input
          name="city"
          placeholder="City"
          value={form.city}
          onChange={change}
          required
        />
        <select name="type" value={form.type} onChange={change}>
          <option value="HOUSE">House</option>
          <option value="APARTMENT">Apartment</option>
          <option value="VILLA">Villa</option>
        </select>
        <input
          name="price"
          type="number"
          placeholder="Price"
          value={form.price}
          onChange={change}
          required
        />
        <label>
          <input
            type="checkbox"
            name="available"
            checked={form.available}
            onChange={change}
          />{" "}
          Available
        </label>
        <br />
        <button type="submit" disabled={loading}>
          {loading ? "Posting..." : "Post Property"}
        </button>
      </form>
    </div>
  );
}
