import React, { useEffect, useState } from 'react';
import { getProperties, toggleProperty } from '../api';

export default function AdminPanel() {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    loadProperties();
  }, []);

  async function loadProperties() {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Not authorized. Please login.');
        return;
      }

      const data = await getProperties(token);
      setProperties(data);
    } catch (err) {
      console.error(err);
      alert(err?.message || 'Failed to fetch properties');
    }
  }

  async function handleToggle(id) {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Not authorized. Please login.');
        return;
      }

      await toggleProperty(token, id);
      await loadProperties();
    } catch (err) {
      console.error(err);
      alert(err?.message || 'Failed to update property');
    }
  }

  return (
    <div>
      <h2>Admin Panel</h2>
      {properties.length === 0 ? (
        <p>No properties found</p>
      ) : (
        <ul>
          {properties.map((p) => (
            <li key={p._id}>
              {p.name} - <b>{p.active ? 'Active' : 'Inactive'}</b>
              <button onClick={() => handleToggle(p._id)}>
                {p.active ? 'Deactivate' : 'Activate'}
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
