import React, {useEffect, useState} from 'react';
import api from '../api';

export default function AdminPanel(){
  const [propsList, setPropsList] = useState([]);

  useEffect(()=>{ fetchAll(); }, []);

  function fetchAll(){
    api.get('/admin/properties').then(r=>setPropsList(r.data)).catch(console.error);
  }

  function toggle(id){
    api.put(`/admin/properties/${id}/toggle`).then(()=>fetchAll()).catch(console.error);
  }

  return (
    <div>
      <h2>Admin — Properties</h2>
      <ul>
        {propsList.map(p=> (
          <li key={p.id}>{p.title} — {p.city} — Available: {p.available ? 'Yes':'No'} <button onClick={()=>toggle(p.id)}>Toggle</button></li>
        ))}
      </ul>
    </div>
  )
}
