import React, { useEffect, useState } from 'react';
import { getAdminProperties, toggleProperty } from '../api';

export default function AdminPanel() {
  const [propsList, setPropsList] = useState([]);

  useEffect(() => {
    fetchAll();
  }, []);

  async function fetchAll() {
    try {
      const data = await getAdminProperties();
      setPropsList(data);
    } catch (err) {
      console.error(err);
    }
  }

  async function toggle(id) {
    try {
      await toggleProperty(id);
      fetchAll(); // refresh after toggle
    } catch (err) {
      console.error(err);
    }
  }

  return (
    <div>
      <h2>Admin — Properties</h2>
      <ul>
        {propsList.map((p) => (
          <li key={p.id}>
            {p.title} — {p.city} — Available: {p.available ? 'Yes' : 'No'}
            <button onClick={() => toggle(p.id)}>Toggle</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
