import React, { useState } from "react";
import { createProperty } from "../api";

export default function PropertyForm() {
  const [form, setForm] = useState({ title: "", description: "", address: "", city: "", price: "", type: "SALE" });

  function change(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function submit(e) {
    e.preventDefault();
    await createProperty({ ...form, price: Number(form.price) });
    alert("Property posted!");
    setForm({ title: "", description: "", address: "", city: "", price: "", type: "SALE" });
  }

  return (
    <div>
      <h2>Post Property</h2>
      <form onSubmit={submit}>
        <input name="title" placeholder="Title" value={form.title} onChange={change} required />
        <textarea name="description" placeholder="Description" value={form.description} onChange={change} />
        <input name="address" placeholder="Address" value={form.address} onChange={change} />
        <input name="city" placeholder="City" value={form.city} onChange={change} />
        <input name="price" placeholder="Price" value={form.price} onChange={change} type="number" />
        <select name="type" value={form.type} onChange={change}>
          <option value="SALE">Sale</option>
          <option value="RENT">Rent</option>
        </select>
        <button type="submit">Post</button>
      </form>
    </div>
  );
}
