// src/components/AdminPanel.js
import React, { useEffect, useState } from "react";
import { getAdminProperties, toggleProperty } from "../api";

export default function AdminPanel() {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch all admin properties
  async function fetchProperties() {
    try {
      setLoading(true);
      const data = await getAdminProperties();
      setProperties(data);
    } catch (err) {
      console.error(err);
      alert("Failed to fetch admin properties. Make sure you are logged in as ADMIN.");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchProperties();
  }, []);

  // Toggle availability
  async function handleToggle(id) {
    try {
      const updated = await toggleProperty(id);
      setProperties((prev) =>
        prev.map((p) => (p.id === updated.id ? updated : p))
      );
    } catch (err) {
      console.error(err);
      alert("Failed to toggle property");
    }
  }

  if (loading) return <div>Loading admin properties...</div>;
  if (!properties.length) return <div>No properties found</div>;

  return (
    <div>
      <h2>Admin Panel</h2>
      <table border="1" cellPadding="6">
        <thead>
          <tr>
            <th>Title</th>
            <th>City</th>
            <th>Type</th>
            <th>Price</th>
            <th>Available</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {properties.map((p) => (
            <tr key={p.id}>
              <td>{p.title}</td>
              <td>{p.city}</td>
              <td>{p.type}</td>
              <td>{p.price}</td>
              <td>{p.available ? "Yes" : "No"}</td>
              <td>
                <button onClick={() => handleToggle(p.id)}>
                  Toggle Availability
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
