import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { searchProperties, getProperties } from "../api";

export default function PropertyList() {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    city: "",
    type: "",
    minPrice: "",
    maxPrice: ""
  });

  useEffect(() => {
    loadProperties();
  }, []);

  async function loadProperties() {
    setLoading(true);
    try {
      const data = await getProperties({ city: "New York" }); // Default city for demo
      setProperties(data);
    } catch (err) {
      console.error(err);
      setProperties([]);
    } finally {
      setLoading(false);
    }
  }

  async function handleSearch() {
    setLoading(true);
    try {
      const searchFilters = {};
      if (filters.city) searchFilters.city = filters.city;
      if (filters.type) searchFilters.type = filters.type;
      if (filters.minPrice) searchFilters.min = filters.minPrice;
      if (filters.maxPrice) searchFilters.max = filters.maxPrice;

      const data = await searchProperties(searchFilters);
      setProperties(data);
    } catch (err) {
      console.error(err);
      alert("Search failed: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  function handleReset() {
    setFilters({
      city: "",
      type: "",
      minPrice: "",
      maxPrice: ""
    });
    loadProperties();
  }

  function handleFilterChange(field, value) {
    setFilters(prev => ({ ...prev, [field]: value }));
  }

  return (
    <div className="properties-container">
      <h2 className="text-center mb-6" style={{ fontSize: '2.5rem', fontWeight: '800', color: '#1a202c' }}>Available Properties</h2>

      {/* Advanced Search Filters */}
      <div className="search-container">
        <h3 className="mb-4" style={{ fontSize: '1.25rem', fontWeight: '600', color: '#374151' }}>Search & Filter Properties</h3>
        <div className="search-grid">
          <div className="form-group">
            <label className="form-label">City</label>
            <input
              type="text"
              placeholder="Enter city name"
              value={filters.city}
              onChange={(e) => handleFilterChange('city', e.target.value)}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Property Type</label>
            <select
              value={filters.type}
              onChange={(e) => handleFilterChange('type', e.target.value)}
            >
              <option value="">All Types</option>
              <option value="HOUSE">House</option>
              <option value="APARTMENT">Apartment</option>
              <option value="VILLA">Villa</option>
            </select>
          </div>
          <div className="form-group">
            <label className="form-label">Min Price ($)</label>
            <input
              type="number"
              placeholder="0"
              value={filters.minPrice}
              onChange={(e) => handleFilterChange('minPrice', e.target.value)}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Max Price ($)</label>
            <input
              type="number"
              placeholder="No limit"
              value={filters.maxPrice}
              onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
            />
          </div>
        </div>
        <div className="flex gap-4 mt-4">
          <button onClick={handleSearch} disabled={loading}>
            {loading ? <span className="loading"></span> : null}
            Search Properties
          </button>
          <button onClick={handleReset} className="btn-secondary">
            Reset Filters
          </button>
        </div>
      </div>

      {/* Property Results */}
      {loading ? (
        <div className="text-center" style={{ padding: '3rem' }}>
          <div className="loading" style={{ width: '40px', height: '40px', margin: '0 auto' }}></div>
          <p style={{ marginTop: '1rem', color: '#6b7280' }}>Loading properties...</p>
        </div>
      ) : properties.length === 0 ? (
        <div className="text-center" style={{ padding: '3rem' }}>
          <p style={{ color: '#6b7280', fontSize: '1.125rem' }}>No properties found. Try adjusting your search filters.</p>
        </div>
      ) : (
        <div className="property-grid">
          {properties.map((property) => (
            <div key={property.id} className="property-card">
              <Link to={`/property/${property.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                <div style={{ marginBottom: '1rem' }}>
                  <h3 style={{ fontSize: '1.25rem', fontWeight: '700', color: '#1f2937', marginBottom: '0.5rem' }}>
                    {property.title}
                  </h3>
                  <p style={{ color: '#6b7280', marginBottom: '0.75rem' }}>
                    {property.address}
                  </p>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
                    <span className="badge badge-info">{property.city}</span>
                    <span className="badge badge-warning">{property.type}</span>
                  </div>
                  <p style={{ color: '#4b5563', marginBottom: '1rem', fontSize: '0.875rem' }}>
                    {property.description?.substring(0, 100)}...
                  </p>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <span style={{ fontSize: '1.5rem', fontWeight: '800', color: '#667eea' }}>
                    ${property.price?.toLocaleString()}
                  </span>
                  <span className={`badge ${property.available ? 'badge-success' : 'badge-danger'}`}>
                    {property.available ? 'Available' : 'Occupied'}
                  </span>
                </div>
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
