import React, { useState } from "react";
import { createProperty } from "../api";

export default function PropertyForm() {
  const [form, setForm] = useState({
    title: '',
    description: '',
    address: '',
    city: '',
    price: '',
    type: 'SALE'
  });
  const [ownerId, setOwnerId] = useState(2);

  function change(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function submit(e) {
    e.preventDefault();
    try {
      await createProperty(form, ownerId);
      alert('Property posted');
      setForm({ title: '', description: '', address: '', city: '', price: '', type: 'SALE' });
    } catch (err) {
      console.error(err);
      alert('Error posting property');
    }
  }

  return (
    <div>
      <h2>Post Property</h2>
      <form onSubmit={submit}>
        <input name="title" placeholder="Title" value={form.title} onChange={change} required />
        <textarea name="description" placeholder="Description" value={form.description} onChange={change} /><br />
        <input name="address" placeholder="Address" value={form.address} onChange={change} /><br />
        <input name="city" placeholder="City" value={form.city} onChange={change} /><br />
        <input name="price" placeholder="Price/month or sale price" value={form.price} onChange={change} type="number" /><br />
        <select name="type" value={form.type} onChange={change}>
          <option value="SALE">Sale</option>
          <option value="RENT">Rent</option>
        </select><br />
        <label>OwnerId (for MVP):</label>
        <input value={ownerId} onChange={(e) => setOwnerId(Number(e.target.value))} type="number" /><br />
        <button type="submit">Post</button>
      </form>
    </div>
  );
}
