import React, { useState } from "react";
import { bookVisit } from "../api";   // import the function

export default function VisitForm({ propertyId }) {
  const [form, setForm] = useState({ tenantName: "", tenantEmail: "", visitDateTime: "" });

  function change(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function submit(e) {
    e.preventDefault();
    try {
      await bookVisit(propertyId, form);
      alert("Visit requested");
    } catch (err) {
      console.error(err);
      alert("Error booking");
    }
  }

  return (
    <form onSubmit={submit}>
      <input name="tenantName" placeholder="Your name" value={form.tenantName} onChange={change} required /><br />
      <input name="tenantEmail" placeholder="Your email" value={form.tenantEmail} onChange={change} required /><br />
      <input name="visitDateTime" placeholder="YYYY-MM-DDTHH:MM:SS" value={form.visitDateTime} onChange={change} required /><br />
      <button type="submit">Request Visit</button>
    </form>
  );
}
