import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getProperty } from '../api';

export default function PropertyList() {
  const [properties, setProperties] = useState([]);
  const [city, setCity] = useState("");

  useEffect(() => {
    fetchAll();
  }, []);

  async function fetchAll() {
    const data = await getAllProperties();
    setProperties(data);
  }

  function search() {
    if (!city) return fetchAll();
    setProperties(properties.filter((p) => p.city.toLowerCase() === city.toLowerCase()));
  }

  return (
    <div>
      <h2>Properties</h2>
      <input placeholder="Search city" value={city} onChange={(e) => setCity(e.target.value)} />
      <button onClick={search}>Search</button>
      <button onClick={fetchAll}>Reset</button>

      <ul>
        {properties.map((p) => (
          <li key={p.id}>
            <Link to={`/property/${p.id}`}>{p.title}</Link> — {p.city} — {p.type} — {p.price}
          </li>
        ))}
      </ul>
    </div>
  );
}
