// src/components/PropertyList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getProperties } from "../api";

export default function PropertyList() {
  const [properties, setProperties] = useState([]);
  const [city, setCity] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAll();
  }, []);

  async function fetchAll() {
    try {
      setLoading(true);
      const data = await getProperties(); // fetch from backend (PostgreSQL)
      setProperties(data);
    } catch (err) {
      console.error(err);
      alert("Failed to load properties: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  async function search() {
    try {
      const data = await getProperties();
      if (city) {
        setProperties(data.filter((p) => p.city.toLowerCase() === city.toLowerCase()));
      } else {
        setProperties(data);
      }
    } catch (err) {
      console.error(err);
      alert("Search failed: " + err.message);
    }
  }

  if (loading) return <div>Loading properties...</div>;

  return (
    <div>
      <h2>Available Properties</h2>
      <div>
        <input
          placeholder="Search city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button onClick={search}>Search</button>
        <button onClick={fetchAll}>Reset</button>
      </div>
      {properties.length === 0 ? (
        <p>No properties found.</p>
      ) : (
        <ul>
          {properties.map((p) => (
            <li key={p.id}>
              <Link to={`/property/${p.id}`}>{p.title}</Link> — {p.city} — {p.type} — ${p.price}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
